import React, { useState } from 'eact';
import img1 from './images/Serenity-spa-about-THERAPISTS1-1024x683.jpg';
import img2 from './images/shutterstock-desktop.webp';
import img3 from './images/t600x362.jpg';

const Service = () => {
  const [cart, setCart] = useState([]);
  const [quantity, setQuantity] = useState({
    RelaxationMassage: 0,
    HotStoneMassage: 0,
    DeepTissueMassage: 0,
  });

  const handleAddToCart = (service) => {
    setCart((prevCart) => [...prevCart, service]);
    setQuantity((prevQuantity) => ({...prevQuantity, [service]: prevQuantity[service] + 1 }));
  };

  const handleRemoveFromCart = (service) => {
    setCart((prevCart) => prevCart.filter((item) => item!== service));
    setQuantity((prevQuantity) => ({...prevQuantity, [service]: prevQuantity[service] - 1 }));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-semibold mb-4">Our Services</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {/* Service Card 1 */}
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <img src={img1} alt="Spa Massage" className="w-full h-64 object-cover object-center" />
          <div className="p-4">
            <h2 className="text-xl font-semibold mb-2">Relaxation Massage</h2>
            <p className="text-gray-700">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
            <button
              className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
              onClick={() => handleAddToCart('RelaxationMassage')}
            >
              Add to Cart
            </button>
            {quantity.RelaxationMassage > 0 && (
              <button
                className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded ml-2"
                onClick={() => handleRemoveFromCart('RelaxationMassage')}
              >
                Remove from Cart
              </button>
            )}
          </div>
        </div>
        {/* Service Card 2 */}
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <img src={img2} alt="Hot Stone Massage" className="w-full h-64 object-cover object-center" />
          <div className="p-4">
            <h2 className="text-xl font-semibold mb-2">Hot Stone Massage</h2>
            <p className="text-gray-700">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
            <button
              className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
              onClick={() => handleAddToCart('HotStoneMassage')}
            >
              Add to Cart
            </button>
            {quantity.HotStoneMassage > 0 && (
              <button
                className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded ml-2"
                onClick={() => handleRemoveFromCart('HotStoneMassage')}
              >
                Remove from Cart
              </button>
            )}
          </div>
        </div>
        {/* Service Card 3 */}
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <img src={img3} alt="Deep Tissue Massage" className="w-full h-64 object-cover object-center" />
          <div className="p-4">
            <h2 className="text-xl font-semibold mb-2">Deep Tissue Massage</h2>
            <p className="text-gray-700">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
            <button
              className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
              onClick={() => handleAddToCart('DeepTissueMassage')}
            >
              Add to Cart
            </button>
            {quantity.DeepTissueMassage > 0 && (
              <button
                className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded ml-2"
                onClick={() => handleRemoveFromCart('DeepTissueMassage')}
                >
                Remove from Cart
              </button>
            )}
          </div>
        </div>
      </div>
      <div className="mt-8">
        <h2 className="text-2xl font-semibold mb-4">Your Cart</h2>
        <ul>
          {cart.map((service, index) => (
            <li key={index} className="flex justify-between mb-2">
              <span>{service}</span>
              <span>x {quantity[service]}</span>
            </li>
          ))}
        </ul>
        {cart.length > 0 && (
          <button
            className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
            onClick={() => alert('Checkout functionality not implemented yet!')}
          >
            Checkout
          </button>
        )}
      </div>
    </div>
  );
};

export default Service;
