import React, { useState, useEffect } from 'react';
import img1 from './images/Serenity-spa-about-THERAPISTS1-1024x683.jpg';
import img2 from './images/shutterstock-desktop.webp';
import img3 from './images/t600x362.jpg';

const Service = () => {
  const services = [
    { id: 1, name: 'Relaxation Massage', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', img: img1 },
    { id: 2, name: 'Hot Stone Massage', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', img: img2 },
    { id: 3, name: 'Deep Tissue Massage', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', img: img3 },
  ];

  const addToCart = (service) => {
    fetch('/api/cart', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ serviceId: service.id, name: service.name }),
    }).then(() => alert(`${service.name} added to cart`));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-semibold mb-4">Our Services</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {services.map(service => (
          <div key={service.id} className="bg-white shadow-lg rounded-lg overflow-hidden">
            <img src={service.img} alt={service.name} className="w-full h-64 object-cover object-center" />
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-2">{service.name}</h2>
              <p className="text-gray-700">{service.description}</p>
              <button
                onClick={() => addToCart(service)}
                className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Service;
